import React from 'react';
import avatar from '../images/avatar.png';
import api from '../utils/Api';

function Main(props) {
    const [userName, setUserName] = React.useState([]);
    const [userDescription, setUserDescription] = React.useState([]);
    const [userAvatar, setUserAvatar] = React.useState([]);
    const [cards, setCards] = React.useState([]);

    React.useEffect(() => {

        api.getInfo()
            .then((data) => {

                setUserDescription(data.about)
                setUserAvatar(data.avatar);
                setUserName(data.name)

            })
            .catch((err) => { console.log(err) })
    })

    React.useEffect(() => {
        api.getAllCards()
        .then((data) => {
            //console.log(data)
            setCards(data)

        })
        .catch((err) => { console.log(err) })
    })

    function getCards(){
return (
    cards.forEach((card) => {
        return(
          <div className="elements__group">
             <img className="elements__image" src={card.link} alt="" />
             <button className="elements__trash-button" type="button"></button>
              <div className="elements__info">
                 <h2 className="elements__caption">{card.name}</h2>
                 <div className="elements__like-container">
                     <button className="elements__like-button" type="button"></button>
                     <p className="elements__like-number">{card.likes.length}</p>
                 </div>
             </div>
          </div>
        )
     })
)
    }

    return (
        <main className="content">
            <section className="profile">
                <div className="profile__container">
                    <button className="profile__avatar-button" type="button" onClick={props.onEditAvatar}>
                        <img className="profile__avatar" src={userAvatar} />
                    </button>
                    <div className="profile__info">
                        <h1 className="profile__name">{userName}</h1>
                        <button className="profile__edit-button" type="button" onClick={props.onEditProfile}></button>
                        <p className="profile__description">{userDescription}</p>
                    </div>
                </div>
                <button className="profile__add-button" type="button" onClick={props.onAddPlace}></button>
            </section>

            <section className="elements">
            <getCards/>



            </section>
        </main>
    )



}

export default Main;
