            let image = {check};
            //let title = "Вы успешно зарегистрировались!";
        }else{
            let image = {cross};
            //let title = "Что-то пошло не так!" +
            //"Попробуйте ещё раз.";
        }
    }



    return (
      <div className={`popup ${props.isOpen ? 'popup_opened' : ''}`}>
        <div className='popup__container'>
            <img src={image} className='popup__image'></img>
