import React from 'react';
import avatar from '../images/avatar.png';
import api from '../utils/Api';

function Main(props) {
const [userName, setUserName] = React.useState([]);
const [userDescription, setUserDescription] = React.useState([]);
const [userAvatar, setUserAvatar] = React.useState([]);

React.useEffect(() => {

    api.getInfo()
    .then((data) => {
        console.log(data.avatar)
       setUserDescription(data.about)
       setUserAvatar(data.avatar);
       setUserName(data.name)
       console.log(setUserAvatar)
    })
    .catch((err) => { console.log(err) })
})

    return (
        <main className="content">
            <section className="profile">
                <div className="profile__container">
                    <button className="profile__avatar-button" type="button" onClick={props.onEditAvatar}>
                        <img className="profile__avatar" src={userAvatar} />
                    </button>
                    <div className="profile__info">
                        <h1 className="profile__name">{setUserName}</h1>
                        <button className="profile__edit-button" type="button" onClick={props.onEditProfile}></button>
                        <p className="profile__description">{setUserDescription}</p>
                    </div>
                </div>
                <button className="profile__add-button" type="button" onClick={props.onAddPlace}></button>
            </section>
            <section className="elements">

            </section>
        </main>
    )



}

export default Main;
