import PopupWithForm from "./PopupWithForm"
import React from "react";

function EditAvatarPopup(props){

    const ref = React.createRef();

    function handleChange(e){
        console.log(ref.current.value)
      return  ref.current.value;

    }

    function handleSubmit(e) {
        e.preventDefault();

        props.onUpdateAvatar({
          avatar: ref.value,
        });
      } 


    return(
        <PopupWithForm title="Обновить аватар" name="avatar" isOpen={props.isOpen} onClosePopup={props.onClose} buttonText="Сохранить">
        <div>
          <input id="avatar-input" className="popup__input popup__input_type_avatar" placeholder="Ссылка на картинку"
            type="url" name="avatar" required minLength="2" maxLength="200" onChange={handleChange}/>
          <span className="avatar-input-error popup__span"></span>
        </div>
      </PopupWithForm>
    )
}

export default EditAvatarPopup;
